// Pump Mode Mapper + Notifications + Dashboard Group Visibility
let mode = String(msg.payload || "").toLowerCase();
let outputs = [null, null, null, null, null];

// Outputs mapping:
// [0] = manual_state (Firebase-out)
// [1] = mode (Firebase-out)
// [2] = schedule_enabled (Firebase-out)
// [3] = notification (dashboard-toast)
// [4] = dashboard-control (show/hide groups)

// Correct Group IDs
const scheduleGroupId = "39dacddc1de95b82";   // Pump Schedule group
const switchGroupId   = "d791a182c3b24495";   // Manual Switch group

// Default state: hide both groups at startup
if (!mode) {
    outputs[4] = {
        payload: {
            groups: {
                hide: [scheduleGroupId, switchGroupId],
                show: []
            }
        }
    };
    return outputs;
}

if (mode === "auto") {
    outputs[0] = { payload: "OFF" };
    outputs[1] = { payload: "auto" };
    outputs[2] = { payload: false };
    outputs[3] = { payload: "‚úÖ Pump set to AUTO mode" };
    outputs[4] = {
        payload: {
            groups: {
                hide: [scheduleGroupId, switchGroupId],
                show: []
            }
        }
    };
}
else if (mode === "manual") {
    outputs[0] = { payload: "OFF" };
    outputs[1] = { payload: "manual" };
    outputs[2] = { payload: false };
    outputs[3] = { payload: "‚úÖ Pump set to MANUAL mode" };
    outputs[4] = {
        payload: {
            groups: {
                hide: [scheduleGroupId],
                show: [switchGroupId]
            }
        }
    };
}
else if (mode === "schedule") {
    outputs[0] = { payload: "OFF" };
    outputs[1] = { payload: "schedule" };
    outputs[2] = { payload: true };
    outputs[3] = { payload: "‚úÖ Pump set to SCHEDULE mode" };
    outputs[4] = {
        payload: {
            groups: {
                hide: [switchGroupId],
                show: [scheduleGroupId]
            }
        }
    };
}
else {
    outputs[3] = { payload: "‚ö†Ô∏è Invalid pump mode selected" };
    outputs[4] = {
        payload: {
            groups: {
                hide: [scheduleGroupId, switchGroupId],
                show: []
            }
        }
    };
}

return outputs;

