// Clamp helper to keep valid range
const clamp = (n, lo, hi) => Math.min(hi, Math.max(lo, parseInt(n, 10) || 0));

// Retrieve stored values
let sh = flow.get("start_hour");
let sm = flow.get("start_minute");
let eh = flow.get("end_hour");
let em = flow.get("end_minute");

// Update based on which input triggered
switch (msg.topic) {
    case "start_hour":
        sh = clamp(msg.payload, 0, 23);
        flow.set("start_hour", sh);
        break;
    case "start_minute":
        sm = clamp(msg.payload, 0, 59);
        flow.set("start_minute", sm);
        break;
    case "end_hour":
        eh = clamp(msg.payload, 0, 23);
        flow.set("end_hour", eh);
        break;
    case "end_minute":
        em = clamp(msg.payload, 0, 59);
        flow.set("end_minute", em);
        break;
}

// Prepare outputs array
let outputs = [];

// If start time complete, send to Firebase
if (sh !== undefined && sm !== undefined) {
    const hh = String(sh).padStart(2, "0");
    const mm = String(sm).padStart(2, "0");
    outputs.push({
        payload: `${hh}:${mm}`,
        method: "set",
        topic: "/control/led/schedules/schedule_2/start_time"
    });
}

// If end time complete, send to Firebase
if (eh !== undefined && em !== undefined) {
    const hh = String(eh).padStart(2, "0");
    const mm = String(em).padStart(2, "0");
    outputs.push({
        payload: `${hh}:${mm}`,
        method: "set",
        topic: "/control/led/schedules/schedule_2/end_time"
    });
}

// Return one message at a time if both updated
if (outputs.length > 1) {
    return [outputs[0], outputs[1]];
} else if (outputs.length === 1) {
    return [outputs[0]];
} else {
    return null;
}

